using System;
using System.Collections.Generic;
using System.Windows.Forms;
using System.IO;
using System.Text.Json;
using System.Text.Json.Serialization;
using System.Linq;

namespace FantasyScenarioSimulation
{
    public partial class Form1 : Form
    {
        private DateTime currentDate;
        private List<Event> events;
        private int orangeSouls;
        private int purpleSouls;
        private List<Servorum> servorumList;
        private int weeksPassed;
        private List<Nation> nations;
        private Random random = new Random();
        private int substance;
        private const int MAX_SUBSTANCE = 15000;
        private const int DAILY_SUBSTANCE_INCREASE = 100;
        private const int SERVORUM_SUBSTANCE_CONSUMPTION = 5;

        public Form1()
        {
            InitializeComponent();
            currentDate = new DateTime(2024, 1, 1); // Start at year 2024, month 1, day 1
            events = new List<Event>();
            orangeSouls = 0;
            purpleSouls = 0;
            servorumList = new List<Servorum>();
            weeksPassed = 0;
            substance = 10000;

            LoadAndProcessNationsData();
            UpdateDateDisplay();
            UpdateCurrencyDisplay();
            UpdateSubstanceDisplay();
            PopulateEvents();
            ClearEvents();
        }

        private void LoadAndProcessNationsData()
        {
            string jsonContent = File.ReadAllText("nations.json");
            var options = new JsonSerializerOptions
            {
                PropertyNameCaseInsensitive = true
            };
            var nationsData = JsonSerializer.Deserialize<NationsData>(jsonContent, options);

            nations = nationsData.Countries.Select(c => new Nation
            {
                Name = c.Name,
                Population = (long)Math.Round((c.Population / 2.0) * (c.Age10To45Percentage / 100.0)),
                Age10To45Percentage = c.Age10To45Percentage
            }).ToList();

            SaveProcessedNations();
        }

        private void SaveProcessedNations()
        {
            File.WriteAllText("processed_nations.json", JsonSerializer.Serialize(new { countries = nations }, new JsonSerializerOptions { WriteIndented = true }));
        }

        private void ClearEvents()
        {
            lstEvents.Items.Clear();
        }

        private void UpdateDateDisplay()
        {
            lblCurrentDate.Text = $"Current Date: {currentDate:yyyy-MM-dd}";
        }

        private void UpdateCurrencyDisplay()
        {
            lblOrangeSouls.Text = $"Orange Souls: {orangeSouls}";
            lblPurpleSouls.Text = $"Purple Souls: {purpleSouls}";
        }

        private void PopulateEvents()
        {
            events.Add(new Event("New Servorums Arrived", DayOfWeek.Sunday, $"{weeksPassed * 1} new Servorums have arrived."));
            events.Add(new Event("Monthly Festival", 1, "It's time for the monthly festival!"));
            UpdateEventList();
        }

        private void UpdateEventList()
        {
            lstEvents.Items.Clear();
            foreach (var evt in events)
            {
                if (evt.ShouldTrigger(currentDate))
                {
                    lstEvents.Items.Add(evt.Name);
                }
            }
        }

        private void AdvanceTime(int days)
        {
            for (int i = 0; i < days; i++)
            {
                currentDate = currentDate.AddDays(1);
                PerformDailyCalculations();

                if (currentDate.DayOfWeek == DayOfWeek.Sunday)
                {
                    weeksPassed++;
                    AddNewServorum();
                    UpdateServorumArrivedEvent();
                }
            }

            UpdateDateDisplay();
            UpdateEventList();
            UpdateServorumList();
            UpdateSubstanceDisplay();
        }

        private void UpdateSubstanceDisplay()
        {
            lblSubstance.Text = $"Substance: {substance}";
        }

        private void PerformDailyCalculations()
        {
            // Increase substance
            substance = Math.Min(substance + DAILY_SUBSTANCE_INCREASE, MAX_SUBSTANCE);

            // Servorum consumption
            int totalConsumption = servorumList.Count * SERVORUM_SUBSTANCE_CONSUMPTION;
            substance = Math.Max(substance - totalConsumption, 0);

        }

        private void BtnAdvanceDay_Click(object sender, EventArgs e)
        {
            AdvanceTime(1);
        }

        private void BtnAdvanceWeek_Click(object sender, EventArgs e)
        {
            AdvanceTime(7);
        }

        private void BtnAdvanceMonth_Click(object sender, EventArgs e)
        {
            AdvanceTime(30); // Simplified; doesn't account for varying month lengths
        }

        private void LstEvents_SelectedIndexChanged(object sender, EventArgs e)
        {
            if (lstEvents.SelectedItem != null)
            {
                string selectedEventName = lstEvents.SelectedItem.ToString();
                Event selectedEvent = events.Find(e => e.Name == selectedEventName);
                if (selectedEvent != null)
                {
                    txtEventDetails.Text = selectedEvent.Description;
                }
            }
        }

        private void AddNewServorum()
        {
            ReloadNationsData();

            int newServorumCount = weeksPassed * 1;
            for (int i = 0; i < newServorumCount; i++)
            {
                Nation selectedNation = SelectNationForServorum();
                if (selectedNation != null)
                {
                    int age = GenerateAge(selectedNation.Age10To45Percentage);
                    servorumList.Add(new Servorum(servorumList.Count + 1, selectedNation.Name, currentDate, age));
                    selectedNation.Population--;
                }
            }
            SaveProcessedNations();
        }

        private int GenerateAge(int age10To45Percentage)
        {
            double youngBias = age10To45Percentage / 100.0;
            double oldBias = 1 - youngBias;

            double randomValue = random.NextDouble();
            double ageBias = Math.Pow(randomValue, oldBias / youngBias);

            return (int)Math.Round(10 + (ageBias * 35));
        }

        private Nation SelectNationForServorum()
        {
            long totalPopulation = nations.Sum(n => (long)n.Population);
            if (totalPopulation <= 0) return null;

            long randomNumber = random.NextInt64(totalPopulation);
            long cumulative = 0;

            foreach (var nation in nations)
            {
                cumulative += nation.Population;
                if (randomNumber < cumulative)
                {
                    return nation;
                }
            }

            return null; // This should never happen, but it's here for safety
        }

        private void UpdateServorumArrivedEvent()
        {
            Event servorumEvent = events.Find(e => e.Name == "New Servorums Arrived");
            if (servorumEvent != null)
            {
                servorumEvent.UpdateDescription($"{weeksPassed * 1} new Servorums have arrived.");
            }
        }

        private void UpdateServorumList()
        {
            lstServorum.Items.Clear();
            foreach (var servorum in servorumList)
            {
                lstServorum.Items.Add(servorum.ID.ToString());
            }
        }

        private void lstServorum_SelectedIndexChanged(object sender, EventArgs e)
        {
            if (lstServorum.SelectedItem != null)
            {
                int selectedId = int.Parse(lstServorum.SelectedItem.ToString());
                Servorum selectedServorum = servorumList.Find(s => s.ID == selectedId);
                if (selectedServorum != null)
                {
                    txtServorumDetails.Text = selectedServorum.GetDetails();
                }
            }
        }
        private void ReloadNationsData()
        {
            string jsonContent = File.ReadAllText("processed_nations.json");
            var options = new JsonSerializerOptions
            {
                PropertyNameCaseInsensitive = true
            };
            var nationsData = JsonSerializer.Deserialize<NationsData>(jsonContent, options);

            nations = nationsData.Countries.Select(c => new Nation
            {
                Name = c.Name,
                Population = c.Population,
                Age10To45Percentage = c.Age10To45Percentage
            }).ToList();
        }
    }

    public class Event
    {
        public string Name { get; private set; }
        public string Description { get; private set; }
        private DayOfWeek? weeklyTrigger;
        private int? monthlyTrigger;

        public Event(string name, DayOfWeek weeklyTrigger, string description)
        {
            Name = name;
            Description = description;
            this.weeklyTrigger = weeklyTrigger;
        }

        public Event(string name, int monthlyTrigger, string description)
        {
            Name = name;
