      const encryptedData = await EncryptedStorage.getItem(key);

      if (encryptedData) {
        // Дешифруємо дані
        const decryptedData = CryptoJS.AES.decrypt(encryptedData, encryptionKey).toString(CryptoJS.enc.Utf8);
        return decryptedData;
      }
    }
  } catch (error) {
    console.error('Помилка при доступі до даних за допомогою біометрії', error);
  }

  return null;
}

async function loadLargeDataWithPassphrase(key, passphrase) {
  try {
    // Генеруємо ключ шифрування на основі passphrase
    const encryptionKey = generateKeyFromPassphrase(passphrase);

    // Отримуємо зашифровані дані з EncryptedStorage
    const encryptedData = await EncryptedStorage.getItem(key);

    if (encryptedData) {
      // Дешифруємо дані
      const decryptedData = CryptoJS.AES.decrypt(encryptedData, encryptionKey).toString(CryptoJS.enc.Utf8);
      return decryptedData;
    }
