```swift
import UIKit

class CustomFlowLayout: UICollectionViewFlowLayout {

    // Normal cell size
    let normalCellWidth: CGFloat = 200
    let normalCellHeight: CGFloat = 225

    // Center cell size
    let centerCellWidth: CGFloat = 270
    let centerCellHeight: CGFloat = 280

    // Normal cell spacing
    let normalSpacing: CGFloat = 30

    // Center cell spacing
    let centerSpacing: CGFloat = 64

    override func prepare() {
        super.prepare()
        // Set scrolling direction
        scrollDirection = .horizontal
        // Calculate item size
        itemSize = CGSize(width: normalCellWidth, height: normalCellHeight)
        // Calculate minimum line spacing
        minimumLineSpacing = normalSpacing
    }

    override func layoutAttributesForElements(in rect: CGRect) -> [UICollectionViewLayoutAttributes]? {
        guard let attributes = super.layoutAttributesForElements(in: rect) else { return nil }

        // Calculate visible rect
        let visibleRect = CGRect(origin: collectionView!.contentOffset, size: collectionView!.bounds.size)

        // Calculate center x
        let centerX = visibleRect.midX

        // Update attributes
        attributes.forEach { attribute in
            // Calculate distance to center
            let distance = abs(attribute.center.x - centerX)

            // Calculate scale
            let scale = 1 - (distance / (collectionView!.bounds.width / 2))

            // Update cell size
            let cellWidth = normalCellWidth + (centerCellWidth - normalCellWidth) * scale
            let cellHeight = normalCellHeight + (centerCellHeight - normalCellHeight) * scale

            // Update cell position
            attribute.frame = CGRect(x: attribute.frame.origin.x, y: (collectionView!.bounds.height - cellHeight) / 2, width: cellWidth, height: cellHeight)

            // Update spacing
            let spacing = normalSpacing + (centerSpacing - normalSpacing) * scale
            if attribute.indexPath.item > 0 {
                let previousAttribute = attributes.first(where: { $0.indexPath.item == attribute.indexPath.item - 1 })
                if let previousAttribute = previousAttribute {
                    attribute.frame.origin.x = previousAttribute.frame.maxX + spacing
                }
            }
        }

        return attributes
    }

    override func shouldInvalidateLayout(forBoundsChange newBounds: CGRect) -> Bool {
        return true
    }
}
